import requests
from configparser import ConfigParser
import google.generativeai as genai


class GeminiModel:
    def __init__(self, api_key):
        # Initialize the modal
        self.api_key = api_key
        genai.configure(api_key=api_key)
        self.model = genai.GenerativeModel(model_name="gemini-1.0-pro")
        self.chat = self.model.start_chat()
    
    def train(self, instructions):
        text = "From now on, please treat me as a"
        for instruction in instructions:
            text += instruction + ","
        response = self.chat.send_message(text[:-1])
        return response.text
        
    def send_message(self, message):
        response = self.chat.send_message(message)
        return response.text
     
    def eval_website(self, website_text, criteria):
        text = "Evaluate the following website and give it a score based on "
        for criterion in criteria:
            text += criterion + ", "
        response = self.chat.send_message(text[:-2] + ":" + website_text)
        return response.text


def gemini_sample_model(query):
    # Load the config info
    config = ConfigParser()
    config.read('config.ini')
    api_key = config['Gemini']['api_key']
    
    genai.configure(api_key=api_key)
    
    model = genai.GenerativeModel(model_name="gemini-1.0-pro")
    
    prompt = "evaluate the following website and give it a score based on readability, informativeness, and correctness:" + query
    convo = model.start_chat()
    convo.send_message(prompt)
    # print(convo.last.text)
    return convo



